/201311/thumb_img/10_thumb_G_1385076352897.jpg', 'images/201311/goods_img/10_G_1385076352496.jpg', 'images/201311/source_img/10_G_1385076352790.png', '1', '', '1', '1', '0', '0', '1241965622', '100', '0', '0', '0', '1', '0', '2', '1390430692', '9', '', '-1', '-1', '0', '0', '', '0', '0', '', '', '0', '');
